import React, { Component } from 'react'

 class Add extends Component {
     constructor(){
         super()
         this.state={
             className:"red"
         }
     }
     changeColor = () => {
        this.setState({className: "blue"});
      }

    render() {
        return (
            <div>
                <button  className="btn btn-primary" onClick={this.state.changeColor}>Change</button>
            </div>
        )
    }
}

export default Add
